mapper.select:
select 
,

from quote_info_en a left join quote_product_en c on c.order_id = a.order_id
where a.order_id = #{orderId,jdbcType=INTEGER}


      and a.customer_id = #{buyerId,jdbcType=VARCHAR}

select 
,

from quote_info_en a left join quote_product_en c on c.order_id = a.order_id
where 

    a.customer_id = #{customerId,jdbcType=VARCHAR}

    and a.order_status = #{orderStatus,jdbcType=INTEGER}

select 

from quote_info_en
where order_id = #{orderId,jdbcType=INTEGER}  
and  customer_id = #{customerId,jdbcType=VARCHAR}

select b.order_factory_id,c.factory_name as order_factory_name,b.order_amount,b.order_time from ( select order_id from quote_info where order_status in
#{item,jdbcType=INTEGER}
)a left join order_factory_tab b on a.order_id = b.order_id LEFT join factory_info_en c on b.order_factory_id = c.factory_id where b.order_time > DATE_SUB(CURDATE(), INTERVAL 3 MONTH) order by b.order_time desc

mapper.update:
