mapper.select:
select  

from quote_info_en   
where  1=1  

   and order_status = #{orderStatus,jdbcType=INTEGER}

 order by create_time desc limit #{start}, #{pageSize}


select count(1) from quote_info_en  where    
csg_order_id = #{csgOrderId,jdbcType=VARCHAR}  

 and order_status = #{orderStatus,jdbcType=INTEGER}

select 
from quote_info_en where order_factory_name is not null
and order_status = 2  and total_amount/10000 >= ${amount}

