mapper.select:
select

from quote_report_type q1
where q1.id = #{id,jdbcType=INTEGER}

select q1.id, q1.order_id, q1.create_date, q1.remark, q1.csg_order_id, q1.project_stage,q1.factory_id, q3.picCount,q4.fileCount,q5.videoCount from quote_report_type q1 left join (select count(1)as picCount,report_type_id,file_type from quote_weekly_report where file_type = 0 GROUP BY report_type_id)q3 on q1.id = q3.report_type_id left join (select count(1)as fileCount,report_type_id,file_type from quote_weekly_report where file_type = 1 GROUP BY report_type_id)q4 on q1.id = q4.report_type_id left join (select count(1)as videoCount,report_type_id,file_type from quote_weekly_report where file_type = 2 GROUP BY report_type_id)q5 on q1.id = q5.report_type_id where 1=1
and q1.csg_order_id = #{csgOrderId,jdbcType=VARCHAR}
and q1.order_id = #{orderId,jdbcType=INTEGER}
order by create_date desc

select q1.id, q1.order_id, q1.create_date, q1.remark, q1.csg_order_id, q1.project_stage,q1.factory_id, q3.picCount,q4.fileCount,q5.videoCount from quote_report_type q1 left join (select count(1)as picCount,report_type_id,file_type from quote_weekly_report where file_type = 0 GROUP BY report_type_id)q3 on q1.id = q3.report_type_id left join (select count(1)as fileCount,report_type_id,file_type from quote_weekly_report where file_type = 1 GROUP BY report_type_id)q4 on q1.id = q4.report_type_id left join (select count(1)as videoCount,report_type_id,file_type from quote_weekly_report where file_type = 2 GROUP BY report_type_id)q5 on q1.id = q5.report_type_id where 1=1
and q1.csg_order_id = #{csgOrderId,jdbcType=VARCHAR}
and q1.order_id = #{orderId,jdbcType=INTEGER}
and q1.factory_id = #{factoryId,jdbcType=VARCHAR} order by create_date desc

select id from quote_report_type
where 1=1

   and csg_order_id = #{csgOrderId,jdbcType=VARCHAR}


  and order_id = #{orderId,jdbcType=INTEGER}

and project_stage = #{projectStage,jdbcType=INTEGER}

mapper.insert:

